import React from "https://esm.sh/react@19.1.0";
import {createRoot} from "https://esm.sh/react-dom@19.1.0/client";
import { DragDropContext, Droppable, Draggable } from "https://esm.sh/@hello-pangea/dnd@18.0.1";

const initialColumns = {
    ai_agents: {
      id: 1,
      name: "AI Agents",
      description: "Responsible for the new AI Agent/Assistance builder",
			items: []
    },
    query_and_widgets: {
      id: 2,
      name: "Query&Widgets",
      description: "Responsible for the developer experience, data sources, queries, JS, Widgets, Templates, Evaluation, and bindings.",
			items: []
    },
    git_and_packages: {
      id: 3,
      name: "Git&Packages",
      description: "Responsible for Git integration, Import/Export of Apps and Packages/Modules",
			items: []
    },
    chat_extension: {
      id: 4,
      name: "Chat Extension",
      description: "This is the old Workflow team. They are working on a new AI experience, but every workflow issue should go to this team.",
			items: []
    },
    platform_admin: {
      id: 5,
      name: "Platform Admin",
      description: "They are responsible for all the Admin Settings, Billing, Audit Logs, and SSO integrations.",
			items: []
    },
    perf_and_observability: {
      id: 6,
      name: "Performance&Observability",
      description: "Responsible for ensuring system performance, monitoring, reliability, and application observability to maintain optimal user experience.",
			items: []
    },
    devops: {
      id: 7,
      name: "DevOps",
      description: "Handles the infrastructure, CI/CD pipelines, deployment automation, and system reliability to support continuous development and operations.",
			items: []
    }
};

function populateColumnsTest (columns, data) {
	if (!Array.isArray(data) || typeof columns !== 'object') {
		throw new Error("Invalid input: columns must be an object and data must be an array");
	}

	const columnKeys = Object.keys(columns);

	 data.forEach(ticket => {
    const randomKey = columnKeys[Math.floor(Math.random() * columnKeys.length)];
    columns[randomKey].items.push(ticket.id);
  });

  return columns;
}

// fake data generator
const getItems = (count, offset = 0) =>
  Array.from({ length: count }, (v, k) => k).map(k => ({
    id: `item-${k + offset}-${new Date().getTime()}`,
    content: `item ${k + offset}`
  }));

const reorder = (list, startIndex, endIndex) => {
  const result = Array.from(list);
  const [removed] = result.splice(startIndex, 1);
  result.splice(endIndex, 0, removed);

  return result;
};

/**
 * Moves an item from one list to another list.
 */
const move = (source, destination, droppableSource, droppableDestination) => {
  const sourceClone = Array.from(source);
  const destClone = Array.from(destination);
  const [removed] = sourceClone.splice(droppableSource.index, 1);

  destClone.splice(droppableDestination.index, 0, removed);

  const result = {};
  result[droppableSource.droppableId] = sourceClone;
  result[droppableDestination.droppableId] = destClone;

  return result;
};

const grid = 8;

const getItemStyle = (isDragging, draggableStyle) => ({
  // some basic styles to make the items look a bit nicer
  userSelect: "none",
  padding: grid * 2,
  margin: `0 0 ${grid}px 0`,

  // change background colour if dragging
  background: isDragging ? "lightgreen" : "grey",

  // styles we need to apply on draggables
  ...draggableStyle
});

const getListStyle = isDraggingOver => ({
  background: isDraggingOver ? "lightblue" : "lightgrey",
  padding: grid,
  width: 250
});


function populateColumns(data) {
  const productAreasMap = {};

  // Initialize product areas with empty items array
  data.product_areas.forEach(area => {
    productAreasMap[area.scenario] = {
      id: area.id,
      name: area.scenario,
      items: []
    };
  });

  // Distribute tickets across their product areas
  data.tickets.forEach(ticket => {
    if (!ticket.product_area) return;

    const areaNames = ticket.product_area.split(',').map(name => name.trim());

    areaNames.forEach(areaName => {
      //if (!productAreasMap[areaName]) {
      //  productAreasMap[areaName] = {
      //    id: null,
      //    name: areaName,
      //    items: []
      //  };
      //}

      productAreasMap[areaName].items.push(ticket);
    });
  });

  // Remove product areas with no items
  Object.keys(productAreasMap).forEach(areaName => {
    if (productAreasMap[areaName].items.length === 0) {
      delete productAreasMap[areaName];
    }
  });

  return productAreasMap;
}


function App() {
  const [columns, setColumns] = React.useState(populateColumns(appsmith.model));
	console.log(columns);

  function onDragEnd(result) {
    const { source, destination } = result;

    // dropped outside the list
    if (!destination) {
      return;
    }
    const sInd = +source.droppableId;
    const dInd = +destination.droppableId;

    if (sInd === dInd) {
      const items = reorder(columns[sInd], source.index, destination.index);
      const newState = [...columns];
      newState[sInd] = items;
      setColumns(newState);
    } else {
      const result = move(columns[sInd], columns[dInd], source, destination);
      const newState = [...columns];
      newState[sInd] = result[sInd];
      newState[dInd] = result[dInd];

      //setColumns(newState);
			setColumns(newState.filter(s => s.length > 0));
    }
  }

  return (
      <div style={{ display: "flex" }}>
        <DragDropContext onDragEnd={onDragEnd}>
          {Object.entries(columns).map(([key, el], ind) => (
            <Droppable key={ind} droppableId={`${ind}`}>
              {(provided, snapshot) => (
                <div
                  ref={provided.innerRef}
                  style={getListStyle(snapshot.isDraggingOver)}
                  {...provided.droppableProps}
                >
								<h2>{key}</h2>
                  {el.items.map((item, index) => (
                    <Draggable
                      key={item.id}
                      draggableId={item.id}
                      index={index}
                    >
                      {(provided, snapshot) => (
                        <div
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                          style={getItemStyle(
                            snapshot.isDragging,
                            provided.draggableProps.style
                          )}
                        >
                          <div
                            style={{
                              display: "flex",
                              justifyContent: "space-around"
                            }}
                          >
                            {item.subject}
                          </div>
                        </div>
                      )}
                    </Draggable>
                  ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          ))}
        </DragDropContext>
      </div>
  );
}

appsmith.onReady(() => {
	/*
	 * This handler function will get called when parent application is ready.
	 * Initialize your component here
	 * more info - https://docs.appsmith.com/reference/widgets/custom#onready
	 */
	createRoot(document.getElementById("root")).render(<App />);
});
